    public static final int RULE_EXTERNAL_ENTITY_VALUE = 63;
    private boolean external_entity_value() throws Exception{
        int ch;
        switch(curState){
            case 0:
                if((ch=codePoint())==EOC){
                    exiting(RULE_EXTERNAL_ENTITY_VALUE, 0);
                    return false;
                }
                if(ch!=EOF && org.apache.xerces.util.XMLChar.isValid(ch)){
                    buffer.push();
                    consume(ch);
                    curState = 1;
                }
                else {
                    buffer.push();
                    handler.externalEntityValue(buffer.pop(0, 0));
                    return !stop;
                }
            case 1:
                if((ch=codePoint())==EOC){
                    exiting(RULE_EXTERNAL_ENTITY_VALUE, 1);
                    return false;
                }
                if(finishAll_CHAR(ch)==EOC){
                    exiting(RULE_EXTERNAL_ENTITY_VALUE, 1);
                    return false;
                }
                handler.externalEntityValue(buffer.pop(0, 0));
                return !stop;
            default:
                throw new Error("impossible state: "+curState);
        }
    }

    public static final int RULE_INT_VALUE = 64;
    private boolean int_value() throws Exception{
        int ch;
        while(true){
            switch(curState){
                case 0:
                    if((ch=codePoint())==EOC){
                        exiting(RULE_INT_VALUE, 0);
                        return false;
                    }
                    if(ch!=EOF && ELEM_CONTENT_CHAR(ch)){
                        buffer.push();
                        consume(ch);
                        curState = 3;
                    }
                    else if(ch=='&'){
                        curState = 0;
                        if(reference()){
                            curState = 6;
                            continue;
                        }else{
                            exiting(RULE_INT_VALUE, 6);
                            return false;
                        }
                    }
                    else {
                        return true;
                    }
                case 3:
                    if((ch=codePoint())==EOC){
                        exiting(RULE_INT_VALUE, 3);
                        return false;
                    }
                    if((ch=finishAll_ELEM_CONTENT_CHAR(ch))==EOC){
                        exiting(RULE_INT_VALUE, 3);
                        return false;
                    }
                    if(ch=='&'){
                        handler.rawValue(buffer.pop(0, 0));
                        curState = 0;
                        if(stop){
                            exiting(RULE_REFERENCE, 0);
                            exiting(RULE_INT_VALUE, 6);
                            return false;
                        }else
                        if(!reference()){
                            exiting(RULE_INT_VALUE, 6);
                            return false;
                        }
                    }
                    else {
                        handler.rawValue(buffer.pop(0, 0));
                        return !stop;
                    }
                case 6:
                    if((ch=codePoint())==EOC){
                        exiting(RULE_INT_VALUE, 6);
                        return false;
                    }
                    if(ch!=EOF && ELEM_CONTENT_CHAR(ch)){
                        buffer.push();
                        consume(ch);
                        curState = 3;
                        continue;
                    }
                    else if(ch=='&'){
                        curState = 0;
                        if(reference()){
                            curState = 6;
                            continue;
                        }else{
                            exiting(RULE_INT_VALUE, 6);
                            return false;
                        }
                    }
                    else {
                        return true;
                    }
                default:
                    throw new Error("impossible state: "+curState);
            }
        }
    }

